//  IMPORTACOES
import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import Icon from 'react-native-vector-icons/MaterialIcons';
import { NavigationContainer } from '@react-navigation/native';
import { createDrawerNavigator } from '@react-navigation/drawer';

//  TELA PRINCIPAL
function App() {
  return (
    <View style={styles.container}>
      <Text>Texto da tela principal</Text>
    </View>
  );
}

//  SEGUNDA TELA
function AppTwo() {
  return (
    <View style={styles.container}>
      <Text>Texto da tela secund√°ria</Text>
    </View>
  );
}

const Drawer = createDrawerNavigator();


{/* MENU LATERAL */}
export default function Menu(){
  return(
    <NavigationContainer>
      <Drawer.Navigator
        initialRouteName="App"
        drawerStyle={{
          backgroundColor: "#261329", //  COR DO FUNDO MENU LATERAL
          paddingVertical: 20         //  PADDING DE CIMA PRA BAIXO
        }}

        drawerContentOptions={{
          activeBackgroundColor: "#000000",
          inactiveTintColor: "#f39772"
        }}>

        {/* ITEM 1 - Home */}
        <Drawer.Screen 
            name="App" 
            component={App} 
            options={
              {
                drawerLabel: (({focused}) => <Text style={{color: focused ? '#313131' : '#fff' }}>Home</Text>),
                drawerIcon: (({focused}) => <Icon color={focused ? '#313131' : '#fff' } name="home" />)
              }
            } 
        />

        {/* ITEM 2 - Perfil */}
        <Drawer.Screen 
          name="MeuApp" 
          component={AppTwo} 
          options={
            {
              drawerLabel: (({focused}) => <Text style={{color: focused ? '#313131' : '#fff' }}>Perfil</Text>),
              drawerIcon: (({focused}) => <Icon color={focused ? '#313131' : '#fff' } name="chat" />)
            }
          }
          />

      </Drawer.Navigator>
    </NavigationContainer>
  )
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#eb6765',
    alignItems: 'center',
    justifyContent: 'center',
    color: '#313131',
  },
});
